mapper.select:
WITH t0 AS ( SELECT f_id AS ID, f_begintime AS startTime, f_endtime AS endTime, f_wyry_id AS userId, f_ddy_id AS ddyId, f_roomid AS roomId, f_filepath AS filePath, f_biz_id AS bizId FROM tb_video_record ORDER BY f_endtime DESC
LIMIT ${limit} OFFSET ${limit} * (${page} - 1)
), t1 AS ( SELECT t0.*, t2.f_username AS username FROM t0 LEFT JOIN tbsys_user t2 ON t0.userId = t2.f_userid ), t3 AS ( SELECT A.* FROM tb_video_devicestate A, ( SELECT f_recordid, MAX ( f_createtime ) f_createtime FROM tb_video_devicestate GROUP BY f_recordid ) b WHERE A.f_recordid = b.f_recordid AND A.f_createtime = b.f_createtime ORDER BY A.f_recordid ), t4 AS ( SELECT t1.*, t3.f_X AS fromX, t3.f_y AS fromY, t3.f_angel AS angel FROM t1 LEFT JOIN t3 ON t1.ID = t3.f_recordid ) SELECT * FROM t4

WITH t1 AS ( SELECT f_id AS ID, f_yy_id AS orderId, f_tbbh AS tbbh, f_layer_id AS layerId, f_state AS STATE, f_ddy_id AS ddyId, f_wyry_id AS userId, f_wyryname AS username, f_serialnumber AS serialnumber, f_conn_state AS connState, f_islock AS islock FROM tb_video_takenumber WHERE f_fhsd_id = '${segmentId}' AND f_state = ${state} ORDER BY f_serialnumber
LIMIT ${limit} OFFSET ${limit} * (${page} - 1)
) SELECT t1.*, ( CASE WHEN t2.f_userid IS NULL THEN 0 ELSE 1 END ) AS islogin FROM t1 LEFT JOIN ( SELECT f_userid FROM tbsys_user WHERE f_updatetime > ( now() - '00:01:01' :: INTERVAL ) AND f_islogin = 1 ) t2 ON t1.userId = t2.f_userid
